import React, { Component } from 'react';

// Имеем доступ к имени покемона
// {this.props.pokemonName}

export default class PokemonInfo extends Component {
  state = { pokemon: null, loading: false, error: null };

  // ! Компонент обновляется когда обновляется пропсы или стэйт
  componentDidUpdate = (prevProps, prevState) => {
    const prevName = prevProps.pokemonName;
    const nextName = this.props.pokemonName;

    // ! Обязательная проверка, чтобы не зациклить компонент
    if (prevName !== nextName) {
      this.setState({ loading: true });
      fetch(`https://pokeapi.co/api/v2/pokemon/${nextName}`)
        .then((res) => {
          if (res.ok) {
            return res.json();
          }

          return Promise.reject(new Error(''));
        })
        .then((pokemon) => this.setState({ pokemon }))
        .catch((error) => this.setState({ error }))
        .finally(() => {
          this.setState({ loading: false });
        });
    }
  };

  render() {
    const { pokemon, loading, error } = this.state;
    const { pokemonName } = this.props;

    return (
      <div>
        {error && (
          <div>
            <h2>Все пропало, покемона с именем {this.props.pokemonName} нет</h2>
          </div>
        )}
        {loading && <div>Loading...</div>}
        {!pokemonName && <div>Enter pokemon name!</div>}
        {pokemon && (
          <h2>
            <p>{pokemon.name}</p>
            <img src={pokemon.sprites.other['official-artwork'].front_default} alt={pokemon.name} width={230} />
          </h2>
        )}
      </div>
    );
  }
}
