import React, { Component } from 'react';

// Имеем доступ к имени покемона
// {this.props.pokemonName}

export default class PokemonInfo extends Component {
  // ! Компонент обновляется когда обновляется пропсы или стэйт
  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.pokemonName !== this.props.pokemonName) {
      console.log('prevProps.pokemonName: ', prevProps.pokemonName);
      console.log('this.props.pokemonName: ', this.props.pokemonName);

      console.log('Изменилось имя покемона. Имена не равны.');
    }
  };

  render() {
    return (
      <div>
        <h1>PokemonInfo</h1>
      </div>
    );
  }
}
