function fetchPokemonName(name) {
  fetch(`https://pokeapi.co/api/v2/pokemon/${name}`).then((res) => {
    if (res.ok) {
      return res.json();
    }

    return Promise.reject(new Error(`Нет покемона с именем "${name}"`));
  });
}

const api = { fetchPokemonName };

export default { api };
