import React, { Component } from "react";

// Имеем доступ к имени покемона
// {this.props.pokemonName}

export default class PokemonInfo extends Component {
  state = {
    pokemon: null,
    error: null,
    status: "idle",
  };

  // ! Компонент обновляется когда обновляется пропсы или стэйт
  componentDidUpdate = (prevProps, prevState) => {
    const prevName = prevProps.pokemonName;
    const nextName = this.props.pokemonName;

    // ! Обязательная проверка, чтобы не зациклить компонент
    if (prevName !== nextName) {
      this.setState({ status: "pending" });
      fetch(`https://pokeapi.co/api/v2/pokemon/${nextName}`)
        .then((res) => {
          if (res.ok) {
            return res.json();
          }

          return Promise.reject(new Error(`Нет покемона с именем ${nextName}`));
        })
        .then((pokemon) => this.setState({ pokemon }))
        .catch((error) => this.setState({ error }))
        .finally(() => {
          this.setState({ loading: false, status: "resolved" });
        });
    }
  };

  render() {
    const { pokemon, error, status } = this.state;

    if (status === "idle") {
      return <div>Enter pokemon name!</div>;
    }
    if (status === "pending") {
      return <div>Loading...</div>;
    }
    if (status === "rejected") {
      return <h2>{error.message}</h2>;
    }
    if (status === "resolved") {
      return (
        <div>
          <h2>
            <p>{pokemon.name}</p>
            <img
              src={pokemon.sprites.other["official-artwork"].front_default}
              alt={pokemon.name}
              width={230}
            />
          </h2>
        </div>
      );
    }
  }
}
