import React from 'react';

import errorImage from '../error.png';

export default function PokemonErrorView({ message }) {
  return (
    <div role="alert">
      <img src={errorImage} width={100} styles={{ marginTop: 100 }} alt="errorimage" />
      <p>{message}</p>
    </div>
  );
}
