import React, { Component } from 'react';
import { FaApple } from 'react-icons/fa';

export default class PokemonForm extends Component {
  state = {
    pokemonName: '',
  };

  handleNameChange = (e) => {
    this.setState({ pokemonName: e.CurrentTarget.value.toLowerCase() });
  };
  handleFormSubmit = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div>
        <form>
          <input type="text" name="PokemonName" value={this.state.pokemonName} />
          <button type="submit">
            <FaApple style={{ width: 20 }} />
            Find pokemon
          </button>
        </form>
      </div>
    );
  }
}
