import React, { Component } from 'react';

export default class PokemonInfo extends Component {
  state = {
    pokemon: null,
    loading: false,
    error: null,
  };

  componentDidUpdate(prevProps, prevState) {
    const prevName = prevProps.pokemonName;
    const nextName = this.props.pokemonName;
    // ! Обязательно делать проверку
    if (prevName !== nextName) {
      console.log('Pokemon name has updated');

      this.setState({ loading: true });
      fetch(`https://pokeapi.co/api/v2/pokemon/${nextName}`)
        .then((res) => res.json())
        .then((pokemon) => this.setState({ pokemon }))
        .catch((error) => this.setState(error))
        .finally(() => this.setState({ loading: false }));
    }
  }

  render() {
    const { loading, pokemon, error } = this.state;
    const { pokemonName } = this.props;
    return (
      <div>
        {error && <div>Everything crashed, there is no pokemon with this name {pokemon.name}</div>}
        <h1>Pokemon Info</h1>
        {loading && <div>Loading...</div>}
        {!pokemonName && <div>Enter pokemon name.</div>}
        {pokemon && (
          <div>
            <p>{pokemon.name}</p>
            <img src={pokemon.sprites.other['official-artwork'].front_default} alt="pokemonImage" width={300} />
          </div>
        )}
      </div>
    );
  }
}
